--
-- Cấu trúc bảng cho bảng `recruitments`
--

CREATE TABLE `recruitments` (
  `id` int(10) UNSIGNED NOT NULL,
  `title` varchar(191) NOT NULL,
  `image_name` varchar(191) NOT NULL,
  `image_path` varchar(191) NOT NULL,
  `contents` text NOT NULL,
  `number_of_applicants` int(11) NOT NULL,
  `application_deadline` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `mucluong` varchar(255) NOT NULL,
  `nganhnghe` varchar(255) NOT NULL,
  `status` tinyint(4) NOT NULL,
  `slug` varchar(200) NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `recruitments`
--

INSERT INTO `recruitments` (`id`, `title`, `image_name`, `image_path`, `contents`, `number_of_applicants`, `application_deadline`, `mucluong`, `nganhnghe`, `status`, `slug`, `created_at`, `updated_at`) VALUES
(1, 'Tuyển dụng vị trí lập trình viên', '377764412_641523244739776_480983576695863628_n.jpg', '/frontEnd/img/tuyendung.jpg', '<p><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Mô tả công việc</strong></span></p><p style=\"text-align:justify;\">- Nắm bắt và hỗ trợ kỹ thuật cho các sản phẩm CNTT của MobiFone</p><p style=\"text-align:justify;\">- Phối hợp phòng Kinh doanh khảo sát nhu cầu khách hàng và phân tích nghiệp vụ yêu cầu phần mềm, viết tài liệu URD, BRD</p><p style=\"text-align:justify;\">- Xây dựng khung triển khai dự án</p><p style=\"text-align:justify;\">- Phối hợp phòng Công nghệ-Kỹ thuật thiết lập hạ tầng phần cứng</p><p style=\"text-align:justify;\">- Thiết kế CSDL và lập trình phần mềm, cập nhật phiên bản</p><p style=\"text-align:justify;\">- Đào tạo sử dụng cho khách hàng</p><p style=\"text-align:justify;\">- Xây dựng các ứng dụng, tiện ích phục vụ SXKD nội bộ</p><p style=\"text-align:justify;\">- Báo cáo kết quả công việc hàng ngày, tuần, tháng</p><p style=\"text-align:justify;\">- Thực hiện các công việc khác theo phân công của Trưởng phòng</p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Yêu cầu cơ bản</strong></span></p><p style=\"text-align:justify;\">- Tuổi: Dưới 35 (Tính đến ngày nộp Hồ sơ).</p><p style=\"text-align:justify;\">- Tốt nghiệp Đại học trở lên chuyên ngành: CNTT/ĐTVT/Toán-Tin hoặc tương đương</p><p style=\"text-align:justify;\">- Thành thạo một trong các ngôn ngữ lập trình: Java, Php, .Net</p><p style=\"text-align:justify;\">- Có kinh nghiệm sử dụng Git</p><p style=\"text-align:justify;\">- Có kinh nghiệm về ReactJs, VueJS, Angular là một lợi thế</p><p style=\"text-align:justify;\">- Kiến thức về lập trình và thiết kế hướng đối tượng (OOP)</p><p style=\"text-align:justify;\">- Kiến thức về cấu trúc dữ liệu &amp; giải thuật, design patterns</p><p style=\"text-align:justify;\">- Kiến thức về quy trình phát triển phần mềm</p><p style=\"text-align:justify;\">- Tư duy tốt, có khả năng nghiên cứu, đánh giá và cập nhật công nghệ mới</p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Quyền lợi</strong></span></p><p style=\"text-align:justify;\">- Mức lương từ 13.000.000-15.000.000. Thu nhập từ 300 – 350 triệu/năm</p><p style=\"text-align:justify;\">- Làm việc trong môi trường chuyên nghiệp, ổn định</p><p style=\"text-align:justify;\">- Chăm sóc sức khỏe bởi gói Bảo hiểm cho cá nhân và gia đình theo chương trình của MobiFone</p><p style=\"text-align:justify;\">- Các chế độ bồi dưỡng, phụ cấp ăn trưa, thuê bao nội bộ của MobiFone</p><p style=\"text-align:justify;\">- Tham gia các hoạt động teambuilding, du lịch, du xuân, nghỉ mát hè… của Công ty</p><p style=\"text-align:justify;\">- Tham dự các khóa đào tạo giúp nâng cao kỹ năng về chuyên môn nghề nghiệp</p><p style=\"text-align:justify;\">- Môi trường làm việc chuyên nghiệp, trẻ trung, hiện đại</p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Làm việc tại:</strong></span><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong> </strong>Trung tâm Kinh doanh Công nghệ số- Văn phòng Công ty tại TP Việt Trì, Phú Thọ</span></p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Thông tin liên hệ: </strong></span><span style=\"background-color:rgb(255,255,255);color:rgb(41,41,41);\">Ngô Thị Thanh Thảo – Chuyên viên phòng Tổng hợp – 0931.699.855 - Email: thao.ngo@mobifone.vn</span></p>', 5, '2023-11-01 08:55:19', '200 - 350 triệu / năm', 'CNTT/ĐTVT/Toán-Tin hoặc tương đương', 1, 'tuyen-dung-vi-tri-lap-trinh-vien-311020231698737618.html', '2023-10-12 08:27:04', '2023-10-31 00:33:38'),
(2, 'Tuyển dụng vị trí chuyên viên khối kinh doanh', 'tuyển dụng', '/frontEnd/img/tuyendung.jpg', '<p><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Mô tả công việc</strong></span></p><p>- Tổ chức triển khai, giám sát và báo cáo kết quả thực hiện các kế hoạch công việc theo mảng chuyên môn được phân công phụ trách.</p><p>- Nắm bắt các sản phẩm dịch vụ GPS/NTS được giao kinh doanh (sản phẩm của MobiFone và sản phẩm hợp tác)</p><p style=\"text-align:justify;\">- Lên kế hoạch kinh doanh sản phẩm và mục tiêu thực hiện doanh thu hàng tháng</p><p style=\"text-align:justify;\">- Tìm kiếm và phát triển nguồn khách hàng tiềm năng</p><p style=\"text-align:justify;\">- Thiết lập cuộc hẹn với khách hàng</p><p style=\"text-align:justify;\">- Tư vấn và thuyết phục khách hàng mua sản phẩm</p><p style=\"text-align:justify;\">- Hoàn thiện hợp đồng với khách hàng</p><p style=\"text-align:justify;\">- Chăm sóc khách hàng trước và sau bán hàng</p><p style=\"text-align:justify;\">- Xây dựng, quản lý và phát triển đội ngũ cộng tác viên bán hàng</p><p style=\"text-align:justify;\">- Nắm bắt thị trường và đối thủ, đề xuất sản phẩm dịch vụ mới và chính sách bán hàng</p><p style=\"text-align:justify;\">- Báo cáo kết quả công việc hàng ngày, tuần, tháng</p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Yêu cầu cơ bản</strong></span></p><p style=\"text-align:justify;\">- Tuổi: Dưới 35 (Tính đến ngày nộp Hồ sơ).</p><p style=\"text-align:justify;\">- Trình độ chuyên môn: KINH TẾ, TÀI CHÍNH, THƯƠNG MẠI ĐIỆN TỬ, MARKETING, TRUYỀN THÔNG, QUẢN TRỊ KINH DOANH, CÔNG NGHỆ THÔNG TIN, TIN HỌC ĐIỆN TỬ VIỄN THÔNG...</p><p style=\"text-align:justify;\">- Tốt nghiệp các trường đại học công lập trong nước (bao gồm cả hệ liên thông). Hoặc tốt nghiệp tại các trường đại học nước ngoài thuộc nhóm 1000 trường Đại học hàng đầu thế giới.</p><p style=\"text-align:justify;\">- Tốt nghiệp các trường ngoài công lập thuộc top 100 trường hàng đầu Việt Nam đối với ứng viên học ngành CNTT hoặc ĐTVT</p><p style=\"text-align:justify;\">- Ưu tiên ứng viên có kinh nghiệm và có hộ khẩu thường trú tại địa bàn</p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Quyền lợi</strong></span></p><p style=\"text-align:justify;\">- Mức lương từ 12.000.000-15.000.000. Thu nhập từ 250 – 350 triệu/năm</p><p style=\"text-align:justify;\">- Làm việc trong môi trường chuyên nghiệp, ổn định</p><p style=\"text-align:justify;\">- Chăm sóc sức khỏe bởi gói Bảo hiểm cho cá nhân và gia đình theo chương trình của MobiFone</p><p style=\"text-align:justify;\">- Các chế độ bồi dưỡng, phụ cấp ăn trưa, thuê bao nội bộ của MobiFone</p><p style=\"text-align:justify;\">- Tham gia các hoạt động teambuilding, du lịch, du xuân, nghỉ mát hè… của Công ty</p><p style=\"text-align:justify;\">- Tham dự các khóa đào tạo giúp nâng cao kỹ năng về chuyên môn nghề nghiệp</p><p style=\"text-align:justify;\">- Môi trường làm việc chuyên nghiệp, trẻ trung, hiện đại</p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Làm việc tại:</strong></span><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong> </strong>Trung tâm Kinh doanh Công nghệ số, Hà Nam, Ninh Bình, Nam Định, Hà Giang, Tuyên Quang, Lào Cai, Sơn La, Điện Biên</span></p><p style=\"text-align:justify;\"><span style=\"background-color:rgb(255,255,255);color:rgb(0,93,178);\"><strong>Thông tin liên hệ: </strong></span><span style=\"background-color:rgb(255,255,255);color:rgb(41,41,41);\">Ngô Thị Thanh Thảo – Chuyên viên phòng Tổng hợp – 0931.699.855 - Email: thao.ngo@mobifone.vn</span></p>', 9, '2023-11-01 08:55:22', '200 - 350 triệu / năm', 'KINH TẾ, TÀI CHÍNH, THƯƠNG MẠI ĐIỆN TỬ, MARKETING, TRUYỀN THÔNG, QUẢN TRỊ KINH DOANH, CÔNG NGHỆ THÔNG TIN, TIN HỌC ĐIỆN TỬ VIỄN THÔNG...', 1, 'tuyen-dung-vi-tri-chuyen-vien-khoi-kinh-doanh-311020231698737652.html', '2023-10-12 08:27:04', '2023-10-31 00:34:12');

